import org.java_websocket.WebSocket;
import org.java_websocket.handshake.ClientHandshake;
import org.java_websocket.server.WebSocketServer;
import java.net.InetSocketAddress;
import java.net.ServerSocketChannel;
import java.nio.ByteBuffer;
import java.nio.channels.ServerSocketChannel;
import java.util.concurrent.atomic.AtomicInteger;

public class SocketActivation extends WebSocketServer {

    private AtomicInteger clients = new AtomicInteger(0);

    public SocketActivation(ServerSocketChannel chan) {
        super(new InetSocketAddress(chan.socket().getLocalPort()));
        try {
            chan.close(); // Close the original ServerSocketChannel
        } catch (Exception e) {
            e.printStackTrace();
        }
    }


    @Override
    public void onOpen(WebSocket conn, ClientHandshake handshake) {
        clients.incrementAndGet();
        conn.send("Welcome to the server!");
        broadcast("New connection: " + conn.getRemoteSocketAddress());
        System.out.println("New connection: " + conn.getRemoteSocketAddress());
    }

    @Override
    public void onClose(WebSocket conn, int code, String reason, boolean remote) {
        clients.decrementAndGet();
        broadcast("Connection closed: " + conn.getRemoteSocketAddress());
        System.out.println("Connection closed: " + conn.getRemoteSocketAddress());
        if (clients.get() == 0) {
            System.out.println("No more clients connected. Shutting down server.");
            stop();
        }
    }

    @Override
    public void onMessage(WebSocket conn, String message) {
        broadcast(message);
        System.out.println("Received message: " + message);
    }

    @Override
    public void onMessage(WebSocket conn, ByteBuffer message) {
        broadcast(message.array());
        System.out.println("Received binary message: " + message.capacity() + " bytes");
    }

    @Override
    public void onError(WebSocket conn, Exception ex) {
        System.err.println("An error occurred: " + ex);
        ex.printStackTrace();
    }

    @Override
