import org.java_websocket.WebSocket;
import org.java_websocket.handshake.ClientHandshake;
import org.java_websocket.server.WebSocketServer;

import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.util.HashMap;
import java.util.Map;

public class ChatServerAttachmentExample extends WebSocketServer {

    private int index = 0;
    private Map<WebSocket, Integer> clientIDs = new HashMap<>();

    public ChatServerAttachmentExample(int port) {
        super(new InetSocketAddress(port));
    }

    public ChatServerAttachmentExample(InetSocketAddress address) {
        super(address);
    }

    @Override
    public void onOpen(WebSocket conn, ClientHandshake handshake) {
        int clientID = index++;
        clientIDs.put(conn, clientID);
        System.out.println("New connection from " + conn.getRemoteSocketAddress() + " with ID " + clientID);
    }

    @Override
    public void onClose(WebSocket conn, int code, String reason, boolean remote) {
        int clientID = clientIDs.get(conn);
        clientIDs.remove(conn);
        System.out.println("Connection closed from " + conn.getRemoteSocketAddress() + " with ID " + clientID + " with code " + code + " and reason " + reason);
    }

    @Override
    public void onMessage(WebSocket conn, String message) {
        int clientID = clientIDs.get(conn);
        System.out.println("Received text message from " + conn.getRemoteSocketAddress() + " with ID " + clientID + ": " + message);
    }

    @Override
    public void onMessage(WebSocket conn, ByteBuffer message) {
        int clientID = clientIDs.get(conn);
        System.out.println("Received binary message from " + conn.getRemoteSocketAddress() + " with ID " + clientID + ": " + message.remaining() + " bytes");
    }

    @Override
    public void onError(WebSocket conn, Exception ex) {
        if (conn != null) {
            int clientID = clientIDs.getOrDefault(conn, -1);
            System.err.println("An error occurred for client " + conn.getRemoteSocketAddress() + " with ID " + clientID + ": " + ex.getMessage());
        } else {
            System.err.println("A server error occurred: " + ex.getMessage());
        }
    }

    @Override
    public void onStart() {
        System.out.println("Server started successfully");
    }

}

