import org.java_websocket.WebSocket;
import org.java_websocket.handshake.ClientHandshake;
import org.java_websocket.server.WebSocketServer;

import java.net.InetSocketAddress;
import java.net.UnknownHostException;
import java.util.concurrent.locks.ReentrantLock;

public class WSocketServer extends WebSocketServer {

    private final ReentrantLock broadcastLock = new ReentrantLock();

    public WSocketServer(int port) throws UnknownHostException {
        super(new InetSocketAddress(port));
    }

    public WSocketServer(InetSocketAddress address) {
        super(address);
    }

    @Override
    public void onOpen(WebSocket conn, ClientHandshake handshake) {
        sendToClient("New connection: " + conn.getRemoteSocketAddress());
    }

    @Override
    public void onClose(WebSocket conn, int code, String reason, boolean remote) {
        sendToClient("Connection closed: " + conn.getRemoteSocketAddress() + " with code " + code + " and reason: " + reason);
    }

    @Override
    public void onMessage(WebSocket conn, String message) {
        sendToClient("Message from " + conn.getRemoteSocketAddress() + ": " + message);
    }

    @Override
    public void onError(WebSocket conn, Exception ex) {
        System.err.println("An error occurred on connection " + (conn != null ? conn.getRemoteSocketAddress() : "Unknown") + ":");
        ex.printStackTrace();
    }

    public void sendToClient(String text) {
        broadcastLock.lock();
        try {
            for (WebSocket conn : connections()) {
                if (conn.isOpen()) {
                    conn.send(text);
                }
            }
        } finally {
            broadcastLock.unlock();
        }
    }
}

