import java.util.HashMap;
import java.util.Map;
import java.util.logging.Level;
import java.util.logging.Logger;

@ServerEndpoint("/websocket")
public class WebsocketServerEndpoint {

    private static final Logger logger = Logger.getLogger(WebsocketServerEndpoint.class.getName());

    private static final Map<String, MessageHandler> HANDLERS = new HashMap<>();

    public static void registerMessageHandler(String messageType, MessageHandler handler) {
        HANDLERS.put(messageType, handler);
    }

    @OnOpen
    public void onOpen(Session session, EndpointConfig config) {
        try {
            String accessToken = session.getRequestParameterMap().get("access_token").get(0);
            // Authenticate using accessToken
            logger.log(Level.INFO, "Client connected: " + session.getId());
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Authentication failed", e);
            try {
                session.close(new CloseReason(CloseReason.CloseCodes.CANNOT_ACCEPT, "Authentication failed"));
            } catch (Exception ex) {
                logger.log(Level.SEVERE, "Error closing session", ex);
            }
        }
    }


    @OnMessage
    public void onMessage(Session session, String message) {
        try {
            // Assuming message format: {"type": "messageType", "payload": ...}
            // Extract message type and payload
            String messageType = ""; // Extract from message
            MessageHandler handler = HANDLERS.get(messageType);
            if (handler != null) {
                Class<? extends Message> messageClass = getMessageClass(handler);
                Message messageObject =  // Deserialize message to messageClass
                handler.handleMessage(messageObject, session);

            } else {
                logger.log(Level.WARNING, "No handler found for message type: " + messageType);
            }
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Error handling message", e);
        }
    }

    @OnClose
    public void onClose(Session session, CloseReason closeReason) {
        logger.log(Level.INFO, "Client disconnected: " + session.getId() + ", Reason: " + closeReason.getReasonPhrase());
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        logger.log(Level.SEVERE, "WebSocket error for session: " + session.getId(), throwable);
    }


    private Class<? extends Message> getMessageClass(MessageHandler handler) {
        return (Class<? extends Message>) ((ParameterizedType) handler.getClass()
                .getGenericInterfaces()[0])
                .getActualTypeArguments()[0];

    }

    public interface Message { }

    public interface MessageHandler<T extends Message> {
        void handleMessage(T message, Session session);
    }
}
