        keyMap.put(27, new byte[]{(byte) 0x1b});
        keyMap.put(13, new byte[]{(byte) 0x0d});
        keyMap.put(37, new byte[]{(byte) 0x1b, (byte) 0x4f, (byte) 0x44});
