import io.grpc.stub.StreamObserver;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.TimeUnit;
import java.util.logging.Logger;

public class GraphQlGrpcClient {

    private final ManagedChannel channel;
    private final GraphQlServiceGrpc.GraphQlServiceStub stub;
    private final CountDownLatch ON_COMPLETE = new CountDownLatch(1);
    private final Logger logger = Logger.getLogger(GraphQlGrpcClient.class.getName());

    public GraphQlGrpcClient(String host, int port) {
        this(ManagedChannelBuilder.forAddress(host, port).usePlaintext().build());
    }

    public GraphQlGrpcClient(ManagedChannel channel) {
        this.channel = channel;
        this.stub = GraphQlServiceGrpc.newStub(channel);
    }

    public void shutdown() throws InterruptedException {
        channel.shutdown().awaitTermination(5, TimeUnit.SECONDS);
    }

    public void query(String query) {
        GraphQlRequest request = GraphQlRequest.newBuilder().setQuery(query).build();
        stub.execute(request, new StreamObserver<GraphQlResponse>() {
            @Override
            public void onNext(GraphQlResponse value) {
                logger.info("Response Received: " + value.getData());
            }

            @Override
            public void onError(Throwable t) {
                logger.severe("Error Occurred: " + t.getMessage());
                ON_COMPLETE.countDown();
            }

            @Override
            public void onCompleted() {
                logger.info("Request Completed");
                ON_COMPLETE.countDown();
            }
        });
