import io.grpc.Server;
import io.grpc.ServerBuilder;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import javax.annotation.PostConstruct;
import javax.annotation.PreDestroy;
import java.io.IOException;
import java.util.Map;
import java.util.concurrent.TimeUnit;

@Slf4j
@Component
public class GrpcLauncher {

    private Server server;
    @Value("${grpc.server.port}")
    private Integer grpcServerPort;
    private static final int DEFAULT_MAX_MESSAGE_SIZE = 10 * 1024 * 1024;


    @Autowired
    public GrpcLauncher() {

    }

    public void grpcStart(Map<String, Object> grpcServiceBeanMap) {
        try {
            ServerBuilder<?> serverBuilder = ServerBuilder.forPort(grpcServerPort)
                    .maxInboundMessageSize(DEFAULT_MAX_MESSAGE_SIZE);

            for (Object grpcServiceBean : grpcServiceBeanMap.values()) {
                serverBuilder.addService((io.grpc.BindableService) grpcServiceBean);
                log.info("Registered gRPC service: {}", grpcServiceBean.getClass().getName());
            }

            server = serverBuilder.build().start();
            log.info("gRPC server started, listening on {}", grpcServerPort);

            Runtime.getRuntime().addShutdownHook(new Thread(() -> {
                log.warn("*** shutting down gRPC server since JVM is shutting down");
                try {
                    if (server != null) {
                        server.shutdown().awaitTermination(5, TimeUnit.SECONDS);
                    }
                } catch (InterruptedException e) {
                    log.error("gRPC server shutdown interrupted: {}", e.getMessage());
                    Thread.currentThread().interrupt();
                }
                log.warn("*** server shut down");
            }));

        } catch (IOException e) {
            log.error("gRPC server startup failed: {}", e.getMessage());
        }
    }


    public void grpcStop() {
        if (server != null) {
            log.info("Shutting down gRPC server...");
            server.shutdown();
            try {
                server.awaitTermination(5, TimeUnit.SECONDS);
            } catch (InterruptedException e) {
                log.error("gRPC server shutdown interrupted: {}", e.getMessage());
                Thread.currentThread().interrupt();
            }
            log.info("gRPC server stopped.");
        }
    }
}

