import org.bouncycastle.asn1.ocsp.OCSPRequest;
import org.bouncycastle.asn1.ocsp.OCSPResponse;
import org.bouncycastle.cert.ocsp.*;
import org.bouncycastle.operator.DigestCalculatorProvider;
import org.bouncycastle.operator.jcajce.JcaDigestCalculatorProviderBuilder;

import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;

public class OCSPClient {

    public static OCSPResp sendOCSPRequest(String url, OCSPReq ocspReq) throws IOException {
        byte[] encodedReq = ocspReq.getEncoded();

        HttpURLConnection connection = (HttpURLConnection) new URL(url).openConnection();
        connection.setDoOutput(true);
        connection.setRequestMethod("POST");
        connection.setRequestProperty("Content-Type", "application/ocsp-request");
        connection.setRequestProperty("Content-Length", String.valueOf(encodedReq.length));

        OutputStream outputStream = connection.getOutputStream();
        outputStream.write(encodedReq);
        outputStream.flush();
        outputStream.close();

        if (connection.getResponseCode() != HttpURLConnection.HTTP_OK) {
            throw new IOException("Unexpected HTTP response code: " + connection.getResponseCode());
        }

        InputStream inputStream = connection.getInputStream();
        ByteArrayOutputStream buffer = new ByteArrayOutputStream();
        byte[] data = new byte[16384];
        int nRead;
        while ((nRead = inputStream.read(data, 0, data.length)) != -1) {
            buffer.write(data, 0, nRead);
        }

        byte[] respBytes = buffer.toByteArray();

        inputStream.close();
        connection.disconnect();

        try {
            DigestCalculatorProvider digCalcProv = new JcaDigestCalculatorProviderBuilder().build();
            return new OCSPRespBuilder().build(respBytes, digCalcProv);
        } catch (OCSPException e) {
            throw new IOException("Error building OCSP response: " + e.getMessage(), e);
        }
    }
}
