import java.io.*;
import java.net.*;
import java.util.concurrent.atomic.AtomicBoolean;

class TcpTransport {

    ServerSocket srv_sock;
    Socket sock;
    OutputStream output;
    InputStream input;
    Thread receiver_thread;
    RtReceiver receiver;

    InetAddress host;
    int port = 7800;
    int out_buf_size = 8192;
    int in_buf_size = 8192;
    boolean server;
    boolean tcp_nodelay;

    interface RtReceiver {
        void receive(byte[] buf, int len);
    }

    public void start(String... options) throws IOException {
        for (int i = 0; i < options.length; i++) {
            if (options[i].equals("--server")) {
                server = true;
            } else if (options[i].equals("--port") && i + 1 < options.length) {
                port = Integer.parseInt(options[++i]);
            } else if (options[i].equals("--host") && i + 1 < options.length) {
                host = InetAddress.getByName(options[++i]);
            } else if (options[i].equals("--out-buf-size") && i + 1 < options.length) {
                out_buf_size = Integer.parseInt(options[++i]);
            } else if (options[i].equals("--in-buf-size") && i + 1 < options.length) {
                in_buf_size = Integer.parseInt(options[++i]);
            } else if (options[i].equals("--tcp-nodelay")) {
                tcp_nodelay = true;
            }
        }

        if (server) {
            srv_sock = new ServerSocket(port);
            sock = srv_sock.accept();
        } else {
            sock = new Socket(host, port);
        }

        sock.setTcpNoDelay(tcp_nodelay);
        sock.setReceiveBufferSize(in_buf_size);
        sock.setSendBufferSize(out_buf_size);

        output = new BufferedOutputStream(sock.getOutputStream(), out_buf_size);
        input = new BufferedInputStream(sock.getInputStream(), in_buf_size);

        receiver_thread = new Thread(new Receiver(input));
        receiver_thread.start();
    }

    public void stop() throws IOException {
        if (receiver_thread != null) {
            receiver_thread.interrupt();
        }
        if (output != null) {
            output.close();
        }
        if (input != null) {
            input.close();
        }
        if (sock != null) {
            sock.close();
        }
        if (srv_sock != null) {
            srv_sock.close();
        }
    }

    public void send(byte[] buf, int offset, int length) throws IOException {
        output.write(buf, offset, length);
        output.flush();
    }

    class Receiver implements Runnable {
        InputStream in;
        AtomicBoolean running = new AtomicBoolean(true);

        Receiver(InputStream in) {
            this.in = in;
        }

        public void run() {
            byte[] buf = new byte[in_buf_size];
            try {
                while (running.get()) {
                    int len = in.read(buf);
                    if (len == -1) {
                        running.set(false);
                        break;
                    }
                    if (receiver != null) {
                        receiver.receive(buf, len);
                    }
                }
            } catch (IOException e) {
                if (running.get()) {
                    e.printStackTrace();
                }
            }
        }
    }
}

