import android.net.Uri;

import java.io.BufferedInputStream;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.OutputStream;
import java.net.ServerSocket;
import java.net.Socket;
import java.net.SocketException;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class HttpMediaStreamer {

    private Uri uri;
    private ServerSocket serverSocket;
    private final String TAG = HttpMediaStreamer.class.getSimpleName();
    private ExecutorService executor = Executors.newSingleThreadExecutor();

    public HttpMediaStreamer(String filename, String mimeType) throws IOException {
        uri = create(filename, mimeType);
    }

    public Uri getUri() {
        return uri;
    }

    public void destroy() {
        if (serverSocket != null) {
            try {
                serverSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        executor.shutdownNow();
    }

    private Uri create(String filename, String mimeType) throws IOException {
        serverSocket = new ServerSocket(0);
        final int port = serverSocket.getLocalPort();
        final String url = "http://localhost:" + port + "/" + filename;

        executor.submit(() -> {
            while (true) {
                try {
                    Socket client = serverSocket.accept();
                    handleClient(client, filename, mimeType);
                    client.close();
                } catch (SocketException e) {
                    break; // Server socket closed
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        });

        return Uri.parse(url);
    }

    private void handleClient(Socket client, String filename, String mimeType) throws IOException {
        BufferedInputStream bis = new BufferedInputStream(new FileInputStream(filename));
        OutputStream os = client.getOutputStream();

        long fileSize = bis.available();

        String headers = "HTTP/1.1 200 OK\r\n" +
                "Content-Type: " + mimeType + "\r\n" +
                "Content-Length: " + fileSize + "\r\n" +
                "Connection: close\r\n" +
                "\r\n";
        os.write(headers.getBytes());

        byte[] buffer = new byte[8192];
        int bytesRead;
        while ((bytesRead = bis.read(buffer)) != -1) {
            os.write(buffer, 0, bytesRead);
        }

        os.flush();
        bis.close();
    }
}

