public class EchoServer extends Thread {
    private int port;
    private ServerSocket serverSocket;
    private List<Thread> threads = Collections.synchronizedList(new ArrayList<>());
    private List<Socket> sockets = Collections.synchronizedList(new ArrayList<>());
    private List<Exception> exceptions = Collections.synchronizedList(new ArrayList<>());
    private AtomicBoolean renegotiate = new AtomicBoolean(false);

    public EchoServer(int port) throws IOException {
        this.port = port;
        this.serverSocket = new ServerSocket(port);
    }

    public EchoServer(SSLServerSocketFactory sslFactory, int port) throws IOException {
        this.port = port;
        this.serverSocket = sslFactory.createServerSocket(port);
    }

    public void renegotiate() {
        renegotiate.set(true);
    }

    @Override
    public void run() {
        while (!serverSocket.isClosed()) {
            try {
                Socket clientSocket = serverSocket.accept();
                sockets.add(clientSocket);
                Thread clientThread = new Thread(() -> {
                    try (clientSocket) {
                        InputStream input = clientSocket.getInputStream();
                        OutputStream output = clientSocket.getOutputStream();
                        byte[] buffer = new byte[1024];
                        int bytesRead;
                        while ((bytesRead = input.read(buffer)) != -1) {
                            output.write(buffer, 0, bytesRead);
                            if (clientSocket instanceof SSLSocket && renegotiate.getAndSet(false)) {
                                ((SSLSocket) clientSocket).startHandshake();
                            }
                        }
                    } catch (Exception e) {
                        exceptions.add(e);
                    } finally {
                        sockets.remove(clientSocket);
                    }
                });
                threads.add(clientThread);
                clientThread.start();
            } catch (IOException e) {
                if (!serverSocket.isClosed()) {
                    exceptions.add(e);
                }
            }
        }
    }


    public void closeConnections() {
        for (Socket socket : sockets) {
            try {
                socket.close();
            } catch (IOException e) {
                exceptions.add(e);
            }
        }
        sockets.clear();
    }

    public void close() {
        try {
            closeConnections();
            serverSocket.close();
            for (Thread thread : threads) {
                thread.interrupt();
            }
        } catch (IOException e) {
            exceptions.add(e);
        }
    }

    public int getExceptionCount() {
        return exceptions.size();
    }
}

