                new Thread(() -> {
                    int bytes_read;
                    try {
                        while ((bytes_read = inFromClient.read(request)) != -1) {
                            outToServer.write(request, 0, bytes_read);
                            outToServer.flush();
                            wasUsedForClientToServer = true;
                        }
                    } catch (IOException ignored) {
                    }
                    try {
                        outToServer.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }).start();

                int bytes_read;
                try {
                    while ((bytes_read = inFromServer.read(reply)) != -1) {
                        outToClient.write(reply, 0, bytes_read);
                        outToClient.flush();
                    }
                } catch (IOException e) {
                } finally {
                    try {
                        if (server != null) server.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
                outToClient.close();
                sClient.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        public void shutdown() {
            try {
                socket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
            interrupt();
        }
    }
