        lenBuf[0] = (byte) (msgBytes.length >>> 8);
        lenBuf[1] = (byte) msgBytes.length;
        out.write(lenBuf);
        out.write(msgBytes);
        out.flush();
    }

    public byte[] receive(boolean wait) throws IOException {
        int bytesRead = 0;
        while (bytesRead < 2) {
            int read = in.read(lenBuf, bytesRead, 2 - bytesRead);
            if (read == -1) {
                if (wait) continue;
                else return null;
            }
            bytesRead += read;
        }

        msgLen = ((lenBuf[0] & 0xFF) << 8) | (lenBuf[1] & 0xFF);
        if (msgLen > MAX_MSG_LEN || msgLen <= 0) {
            throw new IOException("Invalid message length: " + msgLen);
        }

        msgBytes = new byte[msgLen];
        bytesRead = 0;
        while (bytesRead < msgLen) {
            int read = in.read(msgBytes, bytesRead, msgLen - bytesRead);
            if (read == -1) {
                if (wait) continue;
                else throw new IOException("Connection closed prematurely");
            }
            bytesRead += read;
        }
        return msgBytes;
    }


    public void close() throws IOException {
        if (in != null) in.close();
        if (out != null) out.close();
        if (socket != null) socket.close();

        in = null;
        out = null;
        socket = null;
