import java.util.Base64;

public class WebSocketServer {

    private ServerSocket serverSocket;
    private int port;

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        System.out.println("WebSocket server started on port: " + port);
        while (true) {
            handleConnection();
        }
    }

    private void handleConnection() throws IOException {
        Socket clientSocket = serverSocket.accept();
        BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
        DataOutputStream out = new DataOutputStream(clientSocket.getOutputStream());

        String line;
        String key = null;
        while (!(line = in.readLine()).isEmpty()) {
            if (line.startsWith("Sec-WebSocket-Key:")) {
                key = line.substring(19).trim();
            }
        }

        if (key != null) {
            byte[] response = generateWebSocketAcceptKey(key);
            String handshakeResponse = "HTTP/1.1 101 Switching Protocols\r\n" +
                    "Upgrade: websocket\r\n" +
                    "Connection: Upgrade\r\n" +
                    "Sec-WebSocket-Accept: " + Base64.getEncoder().encodeToString(response) + "\r\n\r\n";
            out.writeBytes(handshakeResponse);
            out.flush();


            byte[] frame = createBinaryFrame(new byte[]{1, 2, 3, 4, 5}, true);
            out.write(frame);
            out.flush();

            clientSocket.close();
        }
    }


    private byte[] generateWebSocketAcceptKey(String key) throws IOException {
        String guid = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        String keyConcat = key + guid;
        MessageDigest sha1 = null;
        try {
            sha1 = MessageDigest.getInstance("SHA-1");
        } catch (Exception e) {
            e.printStackTrace();
        }
        return sha1.digest(keyConcat.getBytes("UTF-8"));
    }

    private byte[] createBinaryFrame(byte[] payload, boolean masked) {
        int payloadLength = payload.length;
        int frameSize = 2 + (masked ? 4 : 0) + payloadLength;
        byte[] frame = new byte[frameSize];

        frame[0] = (byte) 0x82; // FIN + Binary Opcode

        if (payloadLength <= 125) {
            frame[1] = (byte) (masked ? 0x80 | payloadLength : payloadLength);
        } else if (payloadLength <= 65535) {
            frame[1] = (byte) (masked ? 0x80 | 126 : 126);
            frame[2] = (byte) ((payloadLength >> 8) & 0xFF);
            frame[3] = (byte) (payloadLength & 0xFF);
        } else {
            frame[1] = (byte) (masked ? 0x80 | 127 : 127);
            // Extended payload length (8 bytes) not implemented for simplicity
        }

        if (masked) {
            byte[] mask = generateMask();
            System.arraycopy(mask, 0, frame, frameSize - payloadLength - 4, 4);
            for (int i = 0; i < payloadLength; i++) {
                frame[frameSize - payloadLength + i] = (byte) (payload[i] ^ mask[i % 4]);
            }
        } else {
            System.arraycopy(payload, 0, frame, frameSize - payloadLength, payloadLength);
        }

        return frame;
    }

    private byte[] generateMask() {
        byte[] mask = new byte[4];
        for (int i = 0; i < 4; i++) {
            mask[i] = (byte) (Math.random() * 256);
        }
        return mask;
    }

    public void stop() throws IOException {
        if (serverSocket != null) {
            serverSocket.close();
        }
    }

    public static void main(String[] args) throws IOException {
        WebSocketServer server = new WebSocketServer(8080);
        server.start();
    }
}

