public abstract class EmbedHttpServer implements Runnable {
    protected int port;
    protected ServerSocket serverSocket;
    private Thread serverThread;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() {
        serverThread = new Thread(this);
        serverThread.start();
    }

    public void stop() throws IOException {
        if (serverSocket != null) {
            serverSocket.close();
        }
    }

    @Override
    public void run() {
        try {
            serverSocket = new ServerSocket(port);
            while (true) {
                Socket clientSocket = serverSocket.accept();
                ExecutorService executor = Executors.newSingleThreadExecutor();
                executor.submit(() -> handleClient(clientSocket));
                executor.shutdown();
            }
        } catch (IOException e) {
            if (!(e instanceof SocketException && e.getMessage().equals("Socket closed"))) {
                e.printStackTrace();
            }
        }
    }


    private void handleClient(Socket clientSocket) {
        try (
                InputStream input = clientSocket.getInputStream();
                OutputStream output = clientSocket.getOutputStream();
                BufferedReader reader = new BufferedReader(new InputStreamReader(input))
        ) {
            String requestLine = reader.readLine();
            if (requestLine == null) return;

            String[] parts = requestLine.split(" ");
            String method = parts[0];
            String path = parts[1];

            HashMap<String, String> headers = new HashMap<>();
            String line;
            while (!(line = reader.readLine()).isEmpty()) {
                String[] headerParts = line.split(": ");
                headers.put(headerParts[0], headerParts[1]);
            }

            Map<String, String> queries = parsePath(path);


            handle(method, queries.get("path"), headers, queries, input, output);

            clientSocket.close();

        } catch (IOException e) {
            e.printStackTrace();
        }
    }


    public abstract void handle(String method, String path, HashMap<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response);

    public Map<String, String> parsePath(String path) {
        Map<String, String> result = new HashMap<>();
        int queryIndex = path.indexOf('?');
        String purePath = (queryIndex == -1) ? path : path.substring(0, queryIndex);
        result.put("path", purePath);

        if (queryIndex != -1) {
            String queryString = path.substring(queryIndex + 1);
            String[] queryParams = queryString.split("&");
            for (String param : queryParams) {
                String[] parts = param.split("=");
                if (parts.length == 2) {
                    result.put(parts[0], parts[1]);
                } else if (parts.length == 1) {
                    result.put(parts[0], "");
                }
            }
        }
        return result;
    }
}

