                InputStream input = contentLength > 0 ? new BodyInputStream(socket.getInputStream(), contentLength) : null;

                handle(method, path, headers, input, output);

                socket.close();

            } catch (IOException e) {
                if (!(e instanceof SocketException && e.getMessage().equals("Socket closed"))) {
                    e.printStackTrace();
                }
                break; 
            }
        }
    }

    class BodyInputStream extends InputStream {
        private final InputStream ins;
        private int remaining;

        BodyInputStream(InputStream ins, int n) {
            this.ins = ins;
            this.remaining = n;
        }

        @Override
        public int available() throws IOException {
            return remaining;
        }

        @Override
        public int read() throws IOException {
            if (remaining <= 0) return -1;
            remaining--;
            return ins.read();
        }

        @Override
        public int read(byte[] b, int off, int len) throws IOException {
            if (remaining <= 0) return -1;
            int bytesRead = ins.read(b, off, Math.min(len, remaining));
            remaining -= bytesRead;
            return bytesRead;
        }

        @Override
        public long skip(long n) throws IOException {
            throw new IOException("Skip not supported");
        }

        @Override
        public void close() throws IOException {
