    private static final int PORT = 8080;
    private static final int TIMEOUT_SECONDS = 10;

    public static void testConnection() throws InterruptedException, IOException {
        CountDownLatch connectedLatch = new CountDownLatch(1);
        CountDownLatch messageReceivedLatch = new CountDownLatch(1);
        Socket socket = new Socket();

        socket.setOnConnected((self, address) -> connectedLatch.countDown());

        socket.connect(new java.net.InetSocketAddress(HOST, PORT));

        if (!connectedLatch.await(TIMEOUT_SECONDS, TimeUnit.SECONDS)) {
            socket.close();
            throw new IOException("Connection timeout");
        }


        OutputStream outputStream = socket.getOutputStream();
        InputStream inputStream = socket.getInputStream();

        String request = "GET / HTTP/1.1\r\nHost: localhost\r\n\r\n";
        outputStream.write(request.getBytes());
        outputStream.flush();


        byte[] buffer = new byte[1024];
        int bytesRead;

        StringBuilder response = new StringBuilder();
        while ((bytesRead = inputStream.read(buffer)) != -1) {
            response.append(new String(buffer, 0, bytesRead));
            if (response.toString().contains("HTTP/1.1")) {
                messageReceivedLatch.countDown();
                break;
            }
        }

        if (!messageReceivedLatch.await(TIMEOUT_SECONDS, TimeUnit.SECONDS)) {
            socket.close();
            throw new IOException("Message receive timeout");
        }

        socket.close();
    }

    public static void main(String[] args) throws InterruptedException, IOException {
        testConnection();
    }
}

