import java.net.InetSocketAddress;
import java.net.Socket;
import java.net.SocketTimeoutException;
import android.os.Handler;
import android.os.Looper;


public class TcpClient {

    private String TAG = "TcpClient";
    private int PACKAGE_HEAD_LENGTH = 8;
    private ReceiveListener receiveListener;

    private String hostip;
    private int port;

    private Socket socketClient;
    private Handler handler = new Handler(Looper.getMainLooper());
    private Thread receiveThread;

    public TcpClient() {
        tryConnect();
    }

    private void tryConnect() {
        new Thread(() -> {
            while (socketClient == null || !socketClient.isConnected()) {
                try {
                    Thread.sleep(2000);
                    connect();
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
            }
        }).start();
    }

    public boolean connect() {
        try {
            socketClient = new Socket();
            socketClient.connect(new InetSocketAddress(hostip, port), 5000);
            if (socketClient.isConnected()) {
                startReceive();
                return true;
            } else {
                return false;
            }
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    public void send(final byte[] data) {
        new Thread(() -> {
            if (socketClient != null && socketClient.isConnected()) {
                try {
                    DataOutputStream writer = new DataOutputStream(socketClient.getOutputStream());
                    writer.write(data);
                    writer.flush();
                } catch (IOException e) {
                    e.printStackTrace();
                    closeAll();
                }
            }
        }).start();
    }


    public interface ReceiveListener {
        void onReceive(byte[] data);
    }

    public void onReceive(ReceiveListener receiveListener) {
        this.receiveListener = receiveListener;
    }

    public void destroy() {
        closeAll();
    }

    private synchronized void closeAll() {
        try {
            if (socketClient != null) {
                socketClient.close();
                socketClient = null;
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void startReceive() {
        receiveThread = new Thread(new Receiver());
        receiveThread.start();
    }

    private int getLengthFromHeadByte(byte[] b) {
        return b[0] << 24 | (b[1] & 0xff) << 16 | (b[2] & 0xff) << 8 | (b[3] & 0xff);
    }

    private int getTypeFromHeadByte(byte[] b) {
        return b[4] << 24 | (b[5] & 0xff) << 16 | (b[6] & 0xff) << 8 | (b[7] & 0xff);
    }

    class Receiver implements Runnable {
        @Override
        public void run() {
            while (socketClient != null && socketClient.isConnected()) {
                try {
                    DataInputStream reader = new DataInputStream(socketClient.getInputStream());
                    byte[] head = new byte[PACKAGE_HEAD_LENGTH];
                    int readHeadSize = reader.read(head);
                    if (readHeadSize == -1) {
                        closeAll();
                    } else if (readHeadSize == PACKAGE_HEAD_LENGTH) {
                        int bodyLength = getLengthFromHeadByte(head);
                        int type = getTypeFromHeadByte(head);
                        byte[] body = new byte[bodyLength];
                        int readBodySize = reader.read(body);
                        if (readBodySize == bodyLength) {
                            if (receiveListener != null) {
                                handler.post(() -> receiveListener.onReceive(body));
                            }
                        }
                    }
                } catch (SocketTimeoutException e) {
                    e.printStackTrace();
                } catch (IOException e) {
                    e.printStackTrace();
                    closeAll();
                }
