    private InputStream is;
    private OutputStream os;
    private boolean connected;

    public void connect(String hostname, int port) throws IOException {
        try {
            InetAddress[] addresses = InetAddress.getAllByName(hostname);
            SocketTimeoutException lastTimeout = null;

            for (InetAddress address : addresses) {
                try {
                    socket = new Socket();
                    socket.connect(new InetSocketAddress(address, port), 30000); // 30 seconds timeout
                    is = socket.getInputStream();
                    os = socket.getOutputStream();
                    connected = true;
                    return; // Connection successful, exit loop
                } catch (SocketTimeoutException e) {
                    lastTimeout = e;
                    // Try next address
                }
            }

            if (lastTimeout != null) {
                throw lastTimeout; // Re-throw the last timeout exception if all addresses fail
            } else {
                throw new IOException("Failed to connect to " + hostname + ":" + port);
            }
        } catch (UnknownHostException e) {
            throw new IOException("Unknown host: " + hostname, e);
        }
    }


    public void close() {
        connected = false;
        try {
            if (is != null) {
                is.close();
            }
        } catch (IOException ignored) {}
        try {
            if (os != null) {
                os.close();
            }
        } catch (IOException ignored) {}
        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException ignored) {}
    }

    public void flush() throws IOException {
        if (!connected) {
            throw new IOException("Not connected.");
        }
        os.flush();
    }

    public boolean isConnected() {
        return connected;
    }

    public int read(byte[] buffer, int start, int len) throws IOException {
        if (!connected) {
            throw new IOException("Not connected.");
        }
        return is.read(buffer, start, len);
