public class NetworkForwarder {

    private final Logger LOGGER = Logger.getLogger(NetworkForwarder.class.getName());
    private final InetAddress destination;
    private final DatagramSocket connectionUdp;
    private final Map<InetSocketAddress, Socket> connectionsTcp = new ConcurrentHashMap<>();

    public NetworkForwarder(Config config) throws SocketException, UnknownHostException {
        this.destination = InetAddress.getByName(config.getDestinationHost());
        this.connectionUdp = new DatagramSocket(config.getLocalPort());
    }

    public void forward(InetSocketAddress source, int port, boolean datagram, byte[] data) {
        try {
            if (datagram) {
                DatagramPacket packet = new DatagramPacket(data, data.length, destination, port);
                connectionUdp.send(packet);
            } else {
                Socket tcpSocket = connectionsTcp.computeIfAbsent(source, k -> {
                    try {
                        return new Socket(destination, port);
                    } catch (IOException e) {
                        LOGGER.warning("Failed to establish TCP connection to " + destination + ":" + port + ": " + e.getMessage());
                        return null;
                    }
                });

                if (tcpSocket != null) {
                    tcpSocket.getOutputStream().write(data);
                }
            }
        } catch (IOException e) {
            LOGGER.warning("Failed to forward data: " + e.getMessage());
        }
    }


    public void disconnect(InetSocketAddress source) {
        Socket tcpSocket = connectionsTcp.remove(source);
        if (tcpSocket != null) {
            try {
                tcpSocket.close();
            } catch (IOException e) {
                LOGGER.warning("Failed to close TCP connection: " + e.getMessage());
            }
        }
    }

