        ByteArrayOutputStream bos = new ByteArrayOutputStream();
        ObjectOutputStream oos = new ObjectOutputStream(bos);
        oos.writeObject(msg);
        byte[] data = bos.toByteArray();
        out.writeInt(data.length);
        out.write(data);
        out.flush();
    }

    static Message readMessage(DataInput in) throws Exception {
        int length = in.readInt();
        byte[] data = new byte[length];
        in.readFully(data);
        ByteArrayInputStream bis = new ByteArrayInputStream(data);
        ObjectInputStream ois = new ObjectInputStream(bis);
        return (Message) ois.readObject();
    }

    public void receive(Message msg) {
        if (msg.type == Message.Type.START) {
            System.out.println("Received START message");
        } else if (msg.type == Message.Type.DATA) {
            System.out.println("Received DATA message: " + msg.data);
        }
