public class GreysConsole {

    private Socket socket;
    private BufferedWriter socketWriter;
    private BufferedReader socketReader;
    private boolean isRunning;

    public GreysConsole(InetSocketAddress serverAddress) throws IOException {
        connect(serverAddress);
    }

    private Socket connect(InetSocketAddress serverAddress) throws IOException {
        this.socket = new Socket();
        this.socket.connect(serverAddress, 5000);
        this.socket.setSoTimeout(5000);
        this.socketWriter = new BufferedWriter(new OutputStreamWriter(this.socket.getOutputStream()));
        this.socketReader = new BufferedReader(new InputStreamReader(this.socket.getInputStream()));
        this.isRunning = true;
        return this.socket;
    }

    public void shutdown() throws IOException {
        this.isRunning = false;
        if (this.socketWriter != null) {
            this.socketWriter.close();
        }
        if (this.socketReader != null) {
            this.socketReader.close();
        }
        if (this.socket != null) {
            this.socket.close();
        }
    }

    public void sendMessage(String message) throws IOException {
        socketWriter.write(message);
        socketWriter.newLine();
        socketWriter.flush();
    }

    public String receiveMessage() throws IOException {
        return socketReader.readLine();
    }

    public boolean isRunning() {
        return isRunning;
    }
}

