import java.io.IOException;
import java.net.*;

public class DNSResolver {

    private int mPort;
    private InetAddress mLocalhost;

    public DNSResolver(int localPort) throws UnknownHostException {
        this.mPort = localPort;
        this.mLocalhost = InetAddress.getByName("127.0.0.1");
    }

    public byte[] processDNS(byte[] payload) throws IOException {
        DatagramSocket socket = new DatagramSocket();
        DatagramPacket requestPacket = new DatagramPacket(payload, payload.length, mLocalhost, mPort);
        socket.send(requestPacket);

        byte[] buffer = new byte[1024];
        DatagramPacket responsePacket = new DatagramPacket(buffer, buffer.length);
        socket.receive(responsePacket);
        socket.close();

        byte[] responseData = new byte[responsePacket.getLength()];
        System.arraycopy(buffer, 0, responseData, 0, responsePacket.getLength());
        return responseData;
    }
}

