        return sock.getLocalSocketAddress();
    }

    public List<? extends Object> clusterMembers() {
        return members;
    }

    public void start(String... opts) throws IOException {
        for (int i = 0; i < opts.length; i++) {
            if (opts[i].equals("-mcast_addr")) {
                host = InetAddress.getByName(opts[++i]);
            } else if (opts[i].equals("-port")) {
                port = Integer.parseInt(opts[++i]);
            } else if (opts[i].equals("-server")) {
                server = true;
            }
        }

        if (host != null) {
            sock = new MulticastSocket(port);
            ((MulticastSocket) sock).joinGroup(host);
        } else {
            sock = new DatagramSocket(port);
        }

        receiver_thread = new Receiver();
        receiver_thread.start();
    }

    public void stop() {
        if (sock != null) {
            sock.close();
        }
    }

    public void send(Object dest, byte[] buf, int offset, int length) throws IOException {
        SocketAddress target = (SocketAddress) dest;
        DatagramPacket packet = new DatagramPacket(buf, offset, length, target);
        sock.send(packet);
    }


    public interface RtReceiver {
        void receive(Object src, byte[] buf, int offset, int length);
    }

    private class Receiver extends Thread {
        public void run() {
            byte[] buf = new byte[65535];
            DatagramPacket packet = new DatagramPacket(buf, buf.length);
            while (true) {
                try {
                    sock.receive(packet);
                    if (receiver != null) {
                        receiver.receive(packet.getSocketAddress(), buf, 0, packet.getLength());
                    }
                } catch (IOException e) {
                    if (!sock.isClosed()) {
                        log.log(Level.SEVERE, "Receiver socket error", e);
                    }
                    break;
                }
            }
        }
    }
}

