interface SntpEventListener {
    void onSntpTimeSync(long time);
    void onSntpFailure(Exception e);
}

class SntpResult {
    public long time;
    public Exception exception;
}

public class SntpImpl implements Sntp {

    public static final int NTP_PORT = 123;
    public static final int NTP_MODE = 3;
    public static final int NTP_VERSION = 3;
    public static final int NTP_PACKET_SIZE = 48;
    public static final long OFFSET_1900_TO_1970 = 2208988800L;


    public SntpResult requestTime(InetAddress address, float rootDelayThreshold, float rootDispersionThreshold, int serverResponseTimeout, int timeout, SntpEventListener listener) {
        SntpResult result = new SntpResult();
        try (DatagramSocket socket = new DatagramSocket()) {
            socket.setSoTimeout(timeout);
            byte[] buffer = new byte[NTP_PACKET_SIZE];
            writeNtpVersion(buffer);
            writeTimeStamp(buffer, 40, System.currentTimeMillis() + OFFSET_1900_TO_1970 * 1000);

            DatagramPacket request = new DatagramPacket(buffer, buffer.length, address, NTP_PORT);
            socket.send(request);

            DatagramPacket response = new DatagramPacket(buffer, buffer.length);
            socket.receive(response);

            double rootDelay = doubleMillis(read(buffer, 24));
            double rootDispersion = doubleMillis(read(buffer, 32));
            long originateTimestamp = readTimeStamp(buffer, 40);
            long receiveTimestamp = readTimeStamp(buffer, 32);
            long transmitTimestamp = readTimeStamp(buffer, 40);

            if (rootDelay > rootDelayThreshold || rootDispersion > rootDispersionThreshold) {
                result.exception = new Exception("Invalid server response: root delay or dispersion exceeded threshold.");
                if (listener != null) listener.onSntpFailure(result.exception);
                return result;
            }

            long destinationTimestamp = System.currentTimeMillis() + OFFSET_1900_TO_1970 * 1000;
            long offset = ((receiveTimestamp - originateTimestamp) + (transmitTimestamp - destinationTimestamp)) / 2;
            long currentTime = System.currentTimeMillis() + offset;

            result.time = currentTime;
            if (listener != null) listener.onSntpTimeSync(currentTime);
            return result;

        } catch (IOException e) {
            result.exception = e;
            if (listener != null) listener.onSntpFailure(e);
            return result;
        }
    }


    private void writeNtpVersion(byte[] buffer) {
        buffer[0] = (byte) (NTP_MODE | (NTP_VERSION << 3));
    }

    private void writeTimeStamp(byte[] buffer, int offset, long time) {
        for (int i = 0; i < 8; i++) {
            buffer[offset + i] = (byte) (time >> (56 - i * 8));
        }
    }

    private long readTimeStamp(byte[] buffer, int offset) {
        long time = 0;
        for (int i = 0; i < 8; i++) {
            time = time << 8 | ui(buffer[offset + i]);
        }
        return time;
    }

    private long read(byte[] buffer, int offset) {
        long value = 0;
        for (int i = 0; i < 4; i++) {
            value = value << 8 | ui(buffer[offset + i]);
        }
        return value;
    }

    private int ui(byte b) {
        return b & 0xFF;
    }

    private double doubleMillis(long value) {
        return ((double) (value >> 16)) + (((double) (value & 0xFFFF)) / 65536.0);
    }

}

