import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketException;
import java.net.UnknownHostException;

public class UDPCommunicator {

    private DatagramSocket socket;
    private boolean isServer;

    public UDPCommunicator(boolean isServer, int port) throws SocketException {
        this.isServer = isServer;
        if (isServer) {
            socket = new DatagramSocket(port);
        } else {
            socket = new DatagramSocket();
        }
    }

    public void sendPacket(byte[] data, String destAddress, int destPort) throws IOException, IllegalStateException {
        if (isServer) {
            throw new IllegalStateException("Cannot send packets in server mode.");
        }
        try {
            InetAddress address = InetAddress.getByName(destAddress);
            DatagramPacket packet = new DatagramPacket(data, data.length, address, destPort);
            socket.send(packet);
        } catch (UnknownHostException e) {
            throw new IOException("Invalid destination address.", e);
        }
    }

    public byte[] receivePacket() throws IOException, IllegalStateException {
        if (!isServer) {
            throw new IllegalStateException("Cannot receive packets in client mode.");
        }
        byte[] buffer = new byte[65507]; // Maximum UDP packet size
        DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
        socket.receive(packet);
        byte[] receivedData = new byte[packet.getLength()];
        System.arraycopy(packet.getData(), packet.getOffset(), receivedData, 0, packet.getLength());
        return receivedData;
    }

    public void close() {
        if (socket != null && !socket.isClosed()) {
            socket.close();
        }
    }
}

