            } catch (SocketTimeoutException e) {
                return true; // Timeout, port might be open
            }
        } catch (PortUnreachableException e) {
            return false; // Port is unreachable
        } catch (IOException e) {
            System.err.println("Error during UDP probe: " + e.getMessage());
            return false;
        }
