import java.util.concurrent.TimeUnit;
import java.util.concurrent.TimeoutException;

public class KafkaTopicCreator {

    private AdminClient adminClient;

    public void createTopic(String topic) throws InterruptedException, ExecutionException, TimeoutException {
        Properties props = new Properties();
        props.put(AdminClientConfig.BOOTSTRAP_SERVERS_CONFIG, "localhost:9092"); // Replace with your Kafka brokers

        adminClient = AdminClient.create(props);

        NewTopic newTopic = new NewTopic(topic, 1, (short) 1); // Adjust partitions and replication factor as needed

        try {
            adminClient.createTopics(Collections.singleton(newTopic)).all().get(10, TimeUnit.SECONDS);
            System.out.println("Topic '" + topic + "' created successfully.");
        } catch (ExecutionException e) {
            if (e.getCause() instanceof TopicExistsException) {
                System.out.println("Topic '" + topic + "' already exists.");
            } else {
                System.err.println("Error creating topic '" + topic + "': " + e.getCause().getMessage());
                throw e;
            }
        } finally {
            adminClient.close(10, TimeUnit.SECONDS);
        }

    }


}
