import java.util.Random;

public class SingleClientUdpTracker {

    private static final Duration CONNECTION_EXPIRATION_TIME = Duration.ofSeconds(60);
    private static final long EPHEMERAL_CONNECTION_ID = 0x41727101980L;
    private static final int CONNECT_CODE = 0;
    private static final int ANNOUNCE_CODE = 1;
    private static final int ERROR_CODE = -1;

    private final DatagramSocket serverSocket;
    private final int interval;
    private final int leechers;
    private final int seeders;
    private volatile boolean shutdown = false;

    private long currentConnection = 0;
    private long connectedOn = 0;


    public SingleClientUdpTracker(int interval, int leechers, int seeders) throws SocketException {
        this.serverSocket = new DatagramSocket();
        this.interval = interval;
        this.leechers = leechers;
        this.seeders = seeders;
    }

    public SocketAddress getServerAddress() {
        return serverSocket.getLocalSocketAddress();
    }

    public void run() throws IOException {
        while (!shutdown) {
            byte[] buffer = new byte[1024];
            DatagramPacket requestPacket = new DatagramPacket(buffer, buffer.length);
            serverSocket.receive(requestPacket);
            byte[] responseData = processRequest(requestPacket.getData());
            DatagramPacket responsePacket = new DatagramPacket(responseData, responseData.length, requestPacket.getAddress(), requestPacket.getPort());
            serverSocket.send(responsePacket);
        }
    }

    private byte[] processRequest(byte[] requestData) {
        ByteBuffer requestBuffer = ByteBuffer.wrap(requestData);
        int connectionId = requestBuffer.getInt();
        int action = requestBuffer.getInt();
        int transactionId = requestBuffer.getInt();

        if (action == CONNECT_CODE) {
            if (connectionId == EPHEMERAL_CONNECTION_ID) {
                long newConnectionId = generateConnectionId();
                currentConnection = newConnectionId;
                connectedOn = Instant.now().toEpochMilli();
                return createConnectResponse(newConnectionId, transactionId);
            }
        } else if (action == ANNOUNCE_CODE) {
            if (connectionId == currentConnection && Instant.now().toEpochMilli() - connectedOn <= CONNECTION_EXPIRATION_TIME.toMillis()) {
                return createAnnounceResponse(connectionId, transactionId);
            }
        }
        return createError(transactionId, "Invalid request");
    }


    private long generateConnectionId() {
        return new Random().nextLong();
    }

    private byte[] createConnectResponse(long connectionId, int transactionId) {
        ByteBuffer buffer = ByteBuffer.allocate(16);
        buffer.putInt(actionToInt(CONNECT_CODE));
        buffer.putInt(transactionId);
        buffer.putLong(connectionId);
        return buffer.array();
    }

    private byte[] createAnnounceResponse(long connectionId, int transactionId) {
        ByteBuffer buffer = ByteBuffer.allocate(20);
        buffer.putInt(actionToInt(ANNOUNCE_CODE));
        buffer.putInt(transactionId);
        buffer.putInt(interval);
        buffer.putInt(leechers);
        buffer.putInt(seeders);
        return buffer.array();
    }

    private byte[] createError(int transactionId, String message) {
        byte[] messageBytes = message.getBytes(StandardCharsets.UTF_8);
        ByteBuffer buffer = ByteBuffer.allocate(8 + messageBytes.length);
        buffer.putInt(actionToInt(ERROR_CODE));
        buffer.putInt(transactionId);
        buffer.put(messageBytes);
        return buffer.array();
    }

    private int actionToInt(int action) {
        return action;
    }

    public void shutdown() {
        shutdown = true;
        serverSocket.close();
    }
}

