        Peer peer = peers.get(0); // Assuming single peer for simplicity
        UDPAnnounceRequestMessage announceRequest = buildAnnounceRequest(event, torrent, peer);
        send(announceRequest.getData());
        ByteBuffer responseBuffer = recv(0);
        TrackerMessage response = TrackerMessage.parse(responseBuffer);
        validateTrackerResponse(response);
        handleTrackerAnnounceResponse(response, inhibitEvents, torrent.getHexInfoHash());
