        stop = true;
        socket.close();
    }

    private UDPAnnounceRequestMessage buildAnnounceRequest(AnnounceRequestMessage.RequestEvent event, AnnounceableInformation torrent, Peer peer) {
        return new UDPAnnounceRequestMessage(connectionId, transactionId, torrent.getInfoHash(), peer.getPeerIdArray(), peer.getUploaded(), peer.getDownloaded(), peer.getLeft(), event.getEventId(), 0, 0, peer.getPort(), 0);
    }


    private void validateTrackerResponse(TrackerMessage message) throws IOException {
        if (message.getTransactionId() != transactionId) {
            throw new IOException("Invalid transaction ID in tracker response");
        }
    }

    private void handleTrackerConnectResponse(TrackerMessage message) {
        this.connectionId = ((UDPConnectResponseMessage) message).getConnectionId();
        this.connectionExpiration = new Date(System.currentTimeMillis() + 60000); // 1 minute expiration
    }

    private void send(ByteBuffer data) throws IOException {
        DatagramPacket packet = new DatagramPacket(data.array(), data.limit(), address);
        socket.send(packet);
    }

    private ByteBuffer recv(int attempt) throws IOException {
        int timeout = UDP_BASE_TIMEOUT_SECONDS * 1000 * (int) Math.pow(2, attempt);
        socket.setSoTimeout(timeout);

        try {
            byte[] buffer = new byte[UDP_PACKET_LENGTH];
            DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
            socket.receive(packet);
            return ByteBuffer.wrap(packet.getData(), 0, packet.getLength()).order(ByteOrder.BIG_ENDIAN);
        } catch (SocketTimeoutException e) {
            if (stop && attempt < UDP_MAX_TRIES_ON_STOPPED) {
                return recv(++attempt);
            } else if (!stop && attempt < UDP_MAX_TRIES) {
                return recv(++attempt);
            }
            throw new IOException("UDP tracker communication timed out", e);
        }
    }


