import java.net.*;
import java.nio.ByteBuffer;
import java.util.*;

public class DnsUdpClient {

    public static final InetSocketAddress[] DNS_SERVERS = {
            new InetSocketAddress("8.8.8.8", 53),
            new InetSocketAddress("8.8.4.4", 53)
    };


    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        try (DatagramSocket socket = new DatagramSocket()) {
            socket.setSoTimeout(5000);
            DatagramPacket requestPacket = new DatagramPacket(query, query.length, dnsServer);
            socket.send(requestPacket);

            byte[] buffer = new byte[1024];
            DatagramPacket responsePacket = new DatagramPacket(buffer, buffer.length);
            socket.receive(responsePacket);

            return toIps(responsePacket.getData());
        }
    }

    private List<Inet4Address> toIps(byte[] buffer) throws UnknownHostException {
        List<Inet4Address> ips = new ArrayList<>();
        int answerCount = (buffer[6] & 0xff) << 8 | (buffer[7] & 0xff);
        int pos = 12;
        for (int i = 0; i < answerCount; i++) {
            while (buffer[pos] != 0) {
                pos += buffer[pos] + 1;
            }
            pos += 5; // Skip QTYPE, QCLASS
            int type = (buffer[pos] & 0xff) << 8 | (buffer[pos + 1] & 0xff);
            if (type == 1) { // A record
                pos += 8; // Skip TYPE, CLASS, TTL, RDLENGTH
                byte[] ipBytes = Arrays.copyOfRange(buffer, pos, pos + 4);
                ips.add((Inet4Address) InetAddress.getByAddress(ipBytes));
                pos += 4;
            } else {
                pos += 2 + 2 + 4 + 2; // Skip TYPE, CLASS, TTL, RDLENGTH
                int rdLength = (buffer[pos - 2] & 0xff) << 8 | (buffer[pos - 1] & 0xff);
                pos += rdLength;
            }
        }
        return ips;
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        try (DatagramSocket socket = new DatagramSocket()) {
            socket.setSoTimeout(5000);
            DatagramPacket requestPacket = new DatagramPacket(query, query.length, dnsServer);
            socket.send(requestPacket);

            byte[] buffer = new byte[1024];
            DatagramPacket responsePacket = new DatagramPacket(buffer, buffer.length);
            socket.receive(responsePacket);

            return toTXT(responsePacket.getData());
        }
    }

    private String toTXT(byte[] buffer) {
        int answerCount = (buffer[6] & 0xff) << 8 | (buffer[7] & 0xff);
        int pos = 12;
        for (int i = 0; i < answerCount; i++) {
            while (buffer[pos] != 0) {
                pos += buffer[pos] + 1;
            }
            pos += 5; // Skip QTYPE, QCLASS
            int type = (buffer[pos] & 0xff) << 8 | (buffer[pos + 1] & 0xff);
            if (type == 16) { // TXT record
                pos += 8; // Skip TYPE, CLASS, TTL, RDLENGTH
                int txtLength = buffer[pos] & 0xff;
                pos++;
                return new String(Arrays.copyOfRange(buffer, pos, pos + txtLength));
            } else {
                pos += 2 + 2 + 4 + 2; // Skip TYPE, CLASS, TTL, RDLENGTH
                int rdLength = (buffer[pos - 2] & 0xff) << 8 | (buffer[pos - 1] & 0xff);
                pos += rdLength;
            }
        }
        throw new RuntimeException("No TXT record found");
    }
}
