    }

    @Override
    public void run() {
        if (mIsCanceled) {
            return;
        }
        mIsExecuted = true;
        start();
    }

    private void start() {
        String proxyUrl = mCacheServer.getProxyUrl(mRawUrl);
        try {
            if (blackList.contains(proxyUrl)) {
                return;
            }
            HttpURLConnection connection = (HttpURLConnection) new URL(proxyUrl).openConnection();
            connection.setConnectTimeout(5000);
            connection.setReadTimeout(5000);
            InputStream is = connection.getInputStream();
            byte[] buffer = new byte[1024];
            int bytesRead;
            int totalBytesRead = 0;
            while ((bytesRead = is.read(buffer)) != -1 && totalBytesRead < 1024 * 100 && !mIsCanceled) { // Preload up to 100KB
                totalBytesRead += bytesRead;
            }
            is.close();
            connection.disconnect();
        } catch (IOException e) {
            blackList.add(proxyUrl);
        }
    }

    public void executeOn(ExecutorService executorService) {
        executorService.execute(this);
    }

    public void cancel() {
        mIsCanceled = true;
    }
}
