    }

    public static String post(String url, Map<String, String> form, Integer readTimeout, Integer connectTimeout) {
        HttpURLConnection con = null;
        try {
            con = (HttpURLConnection) new URL(url).openConnection();
            con.setRequestMethod("POST");
            con.setReadTimeout(readTimeout);
            con.setConnectTimeout(connectTimeout);
            con.setDoOutput(true);

            if (form != null && !form.isEmpty()) {
                StringBuilder formData = new StringBuilder();
                for (Map.Entry<String, String> entry : form.entrySet()) {
                    if (formData.length() > 0) {
                        formData.append("&");
                    }
                    formData.append(URLEncoder.encode(entry.getKey(), StandardCharsets.UTF_8.toString()));
                    formData.append("=");
                    formData.append(URLEncoder.encode(entry.getValue(), StandardCharsets.UTF_8.toString()));
                }

                try (DataOutputStream out = new DataOutputStream(con.getOutputStream())) {
                    out.write(formData.toString().getBytes(StandardCharsets.UTF_8));
                }
            }


            try (BufferedReader in = new BufferedReader(new InputStreamReader(con.getInputStream()))) {
                String inputLine;
                StringBuilder response = new StringBuilder();
                while ((inputLine = in.readLine()) != null) {
                    response.append(inputLine);
                }
                return response.toString();
            }

