            properties.put(AdminClientConfig.SECURITY_PROTOCOL_CONFIG, kafkaClientInfo.getSecurityProtocol());
        }
        if (kafkaClientInfo.getSslTruststoreLocation() != null && kafkaClientInfo.getSslTruststorePassword() != null) {
