public class HttpUtils {

    public static String parseUrlArgs(String responseUrl, JsonObject jsonObject) {
        try {
            URL url = new URL(responseUrl);
            String query = url.getQuery();
            if (query != null) {
                String[] pairs = query.split("&");
                for (String pair : pairs) {
                    int idx = pair.indexOf("=");
                    String key = idx > 0 ? URLDecoder.decode(pair.substring(0, idx), "UTF-8") : pair;
                    String value = idx > 0 && pair.length() > idx + 1 ? URLDecoder.decode(pair.substring(idx + 1), "UTF-8") : null;
                    jsonObject.addProperty(key, value);
                }
            }
            return url.getProtocol() + "://" + url.getHost() + url.getPath();

        } catch (MalformedURLException | UnsupportedEncodingException e) {
            return responseUrl; // Return original URL if parsing fails
        }
    }


    public static String post(String httpUrl, String content) throws Exception {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(httpUrl);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            connection.setRequestProperty("Content-Type", "application/json");

            if (content != null && !content.isEmpty()) {
                try (OutputStream os = connection.getOutputStream()) {
                    byte[] input = content.getBytes("utf-8");
                    os.write(input, 0, input.length);
                }
            }

            int responseCode = connection.getResponseCode();
            if (responseCode != HttpURLConnection.HTTP_OK) {
                throw new Exception("HTTP POST request failed with response code: " + responseCode);
            }

            try (BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()))) {
                String inputLine;
                StringBuilder response = new StringBuilder();
                while ((inputLine = in.readLine()) != null) {
                    response.append(inputLine);
                }
                return response.toString();
            }

        } catch (IOException e) {
            throw new Exception("Error during HTTP POST request: " + e.getMessage(), e);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }
}

