import android.content.Context;
import android.net.Uri;
import android.util.Log;

import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpStatusChecker {

    private Context context;
    private String url;

    public HttpStatusChecker(Context context, String url) {
        this.context = context;
        this.url = url;
    }

    public StatusResult checkStatus() {
        try {
            HttpURLConnection connection = (HttpURLConnection) new URL(url).openConnection();
            connection.setInstanceFollowRedirects(false);
            connection.connect();

            int statusCode = connection.getResponseCode();
            String message = getHttpStatusMessage(statusCode);

            String redirectionUrl = null;
            if (statusCode >= 300 && statusCode < 400) {
                redirectionUrl = connection.getHeaderField("Location");
                if (redirectionUrl != null && !Uri.parse(redirectionUrl).isAbsolute()) {
                    redirectionUrl = Uri.parse(url).buildUpon().path(redirectionUrl).build().toString();
                }
            }
            connection.disconnect();
            return new StatusResult(message, redirectionUrl);

        } catch (IOException e) {
            Log.e("HttpStatusChecker", "Error checking URL: " + url, e);
            return new StatusResult(context.getString(R.string.http_status_error_message, e.getMessage()), null);
        } catch (Exception e) {
            Log.e("HttpStatusChecker", "Unexpected error checking URL: " + url, e);
            return new StatusResult(context.getString(R.string.http_status_unexpected_error_message, e.getMessage()), null);
        }
    }

    private String getHttpStatusMessage(int statusCode) {
        switch (statusCode) {
            case HttpURLConnection.HTTP_OK:
                return context.getString(R.string.http_status_ok_message);
            case HttpURLConnection.HTTP_MOVED_PERM:
            case HttpURLConnection.HTTP_MOVED_TEMP:
            case HttpURLConnection.HTTP_SEE_OTHER:
                return context.getString(R.string.http_status_redirect_message);
            case HttpURLConnection.HTTP_NOT_FOUND:
                return context.getString(R.string.http_status_not_found_message);
            case HttpURLConnection.HTTP_INTERNAL_ERROR:
                return context.getString(R.string.http_status_internal_error_message);
            default:
                return context.getString(R.string.http_status_other_message, statusCode);
        }
    }


    public static class StatusResult {
        public final String message;
        public final String redirectionUrl;

        public StatusResult(String message, String redirectionUrl) {
            this.message = message;
            this.redirectionUrl = redirectionUrl;
        }
    }
}
