import javax.net.ssl.HttpsURLConnection;
import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;
import java.net.URLConnection;
import java.nio.charset.StandardCharsets;
import java.util.Map;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class HttpURLConnectionUtils {

    private static final String TAG = "HttpURLConnectionUtils";
    private static final int TIMEOUT_IN_MILLIONS = 5000;
    private static final String BAIDU_URL = "https://www.baidu.com";
    private final ExecutorService executor = Executors.newFixedThreadPool(4);


    public interface Callback {
        void onResponse(String result, long response);
        void onFail(Throwable error);
    }

    public interface TimeCallback {
        void onResponse(long millis);
        void onFail(Throwable error);
    }

    public void doGetAsync(String urlStr, Callback callback) {
        request("GET", urlStr, null, null, callback);
    }

    public void doPostAsync(String urlStr, String params, Callback callback) {
        request("POST", urlStr, null, params, callback);
    }


    public void getNetTime(TimeCallback callback) {
        reqNetTime(BAIDU_URL, callback);
    }

    public void getNetTime(String urlStr, TimeCallback callback) {
        reqNetTime(urlStr, callback);
    }

    private void request(String method, String urlStr, Map<String, String> headers, String params, Callback callback) {
        executor.execute(() -> {
            try {
                URL url = new URL(urlStr);
                URLConnection urlConnection = url.openConnection();
                HttpURLConnection connection = (HttpURLConnection) urlConnection;

                connection.setRequestMethod(method);
                connection.setReadTimeout(TIMEOUT_IN_MILLIONS);
                connection.setConnectTimeout(TIMEOUT_IN_MILLIONS);
                if (headers != null) {
                    for (Map.Entry<String, String> entry : headers.entrySet()) {
                        connection.setRequestProperty(entry.getKey(), entry.getValue());
                    }
                }

                if ("POST".equals(method) && params != null) {
                    connection.setDoOutput(true);
                    OutputStream os = connection.getOutputStream();
                    BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(os, StandardCharsets.UTF_8));
                    writer.write(params);
                    writer.flush();
                    writer.close();
                    os.close();
                }

                connection.connect();
                int responseCode = connection.getResponseCode();
                if (responseCode == HttpURLConnection.HTTP_OK) {
                    InputStream in = connection.getInputStream();
                    BufferedReader reader = new BufferedReader(new InputStreamReader(in));
                    StringBuilder response = new StringBuilder();
                    String line;
                    while ((line = reader.readLine()) != null) {
                        response.append(line);
                    }
                    reader.close();
                    in.close();

                    if (callback != null) {
                        callback.onResponse(response.toString(), connection.getDate());
                    }
                } else {
                    if (callback != null) {
                        callback.onFail(new Exception("HTTP error code: " + responseCode));
                    }
                }
                connection.disconnect();

            } catch (Exception e) {
                if (callback != null) {
                    callback.onFail(e);
                }
            }
        });
    }

    private void reqNetTime(String urlStr, TimeCallback callback) {
        executor.execute(() -> {
            try {
                URL url = new URL(urlStr);
                HttpsURLConnection connection = (HttpsURLConnection) url.openConnection();
                connection.setRequestMethod("GET");
                connection.setConnectTimeout(TIMEOUT_IN_MILLIONS);
                connection.connect();
                long date = connection.getDate();
                if (callback != null) {
                    callback.onResponse(date);
                }
            } catch (IOException e) {
                if (callback != null) {
                    callback.onFail(e);
                }
            }
        });
    }
}

