        ByteArrayOutputStream baos = null;

        try {
            connection = createConnection(httpRequestInfo);
            configureRequest(connection, httpRequestInfo, cookieValue);
            sendRequestBody(connection, httpRequestInfo);

            int responseCode = connection.getResponseCode();

            inputStream = (responseCode >= 400) ? connection.getErrorStream() : connection.getInputStream();

            baos = new ByteArrayOutputStream();
            byte[] buffer = new byte[4096];
            int bytesRead;
            while ((bytesRead = inputStream.read(buffer)) != -1) {
                baos.write(buffer, 0, bytesRead);
            }
            return baos.toString("UTF-8");

        } catch (Exception e) {
            e.printStackTrace();
            return null;
        } finally {
            closeResources(connection, inputStream, baos);
        }
    }


    private HttpURLConnection createConnection(HttpRequestInfo httpRequestInfo) throws IOException {
        URL url = new URL(httpRequestInfo.getUrl());
        HttpURLConnection connection;

        if (config.getProxyHost() != null && config.getProxyPort() != 0) {
            Proxy proxy = new Proxy(Proxy.Type.HTTP, new InetSocketAddress(config.getProxyHost(), config.getProxyPort()));
            connection = (HttpURLConnection) url.openConnection(proxy);
        } else {
            connection = (HttpURLConnection) url.openConnection();
        }

        if (httpRequestInfo.getUrl().startsWith("https") && config.isDisableSslVerification()) {
            disableSslVerification((HttpsURLConnection) connection);
        }
        connection.setConnectTimeout(config.getConnectionTimeout());
        connection.setReadTimeout(config.getReadTimeout());

        return connection;
    }

    private void configureRequest(HttpURLConnection connection, HttpRequestInfo httpRequestInfo, String cookieValue) throws IOException {
        connection.setRequestMethod(httpRequestInfo.getMethod());
        for (Map.Entry<String, String> header : httpRequestInfo.getHeaders().entrySet()) {
            connection.setRequestProperty(header.getKey(), header.getValue());
        }
        configureCookie(httpRequestInfo.getHeaders(), cookieValue);

        if (httpRequestInfo.getRequestBody() != null) {
            connection.setDoOutput(true);
        }
    }

    private void configureCookie(Map<String, String> headers, String cookieValue) {
        if (cookieValue != null && !cookieValue.isEmpty()) {
            if (config.getCookieName() != null && !config.getCookieName().isEmpty()) {
                headers.put("Cookie", config.getCookieName() + "=" + cookieValue);
            } else {
                headers.put("Cookie", cookieValue);
            }
        }
    }

    private void sendRequestBody(HttpURLConnection connection, HttpRequestInfo httpRequestInfo) throws IOException {
        if (httpRequestInfo.getRequestBody() != null) {
            try (OutputStream os = connection.getOutputStream()) {
                byte[] input = httpRequestInfo.getRequestBody().getBytes("utf-8");
                os.write(input, 0, input.length);
            }
        }
    }


    private void closeResources(HttpURLConnection connection, InputStream inputStream, ByteArrayOutputStream baos) {
        try {
            if (baos != null) {
                baos.close();
            }
            if (inputStream != null) {
                inputStream.close();
            }
            if (connection != null) {
                connection.disconnect();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void disableSslVerification(HttpsURLConnection connection) {
        try {
            TrustManager[] trustAllCerts = new TrustManager[]{new X509TrustManager() {
                public java.security.cert.X509Certificate[] getAcceptedIssuers() {
                    return null;
