import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardCopyOption;
import java.util.Optional;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class HttpDownloader {
    private static final Logger logger = LoggerFactory.getLogger(HttpDownloader.class);
    private static final int MAXIMUM_REDIRECTS = 8;

    public void download(String targetUrlString, Path path, String userAgent) throws IOException {
        String currentUrl = targetUrlString;
        int redirectCount = 0;

        while (redirectCount <= MAXIMUM_REDIRECTS) {
            Optional<String> nextUrl = getDownloadedOrNextUrl(currentUrl, path, userAgent);

            if (!nextUrl.isPresent()) {
                break;
            }

            currentUrl = nextUrl.get();
            redirectCount++;
        }

        if (redirectCount > MAXIMUM_REDIRECTS) {
            throw new IOException("Maximum redirects exceeded");
        }
    }

    private Optional<String> getDownloadedOrNextUrl(String urlString, Path path, String userAgent) throws IOException {
        URL url = new URL(urlString);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setRequestProperty("User-Agent", userAgent);

        int responseCode = connection.getResponseCode();

        if (responseCode == HttpURLConnection.HTTP_OK) {
            Files.copy(connection.getInputStream(), path, StandardCopyOption.REPLACE_EXISTING);
            return Optional.empty();
        } else if (responseCode == HttpURLConnection.HTTP_MOVED_TEMP || responseCode == HttpURLConnection.HTTP_MOVED_PERM) {
            String redirectUrl = connection.getHeaderField("Location");
            logger.info("Redirecting to: {}", redirectUrl);
            return Optional.of(redirectUrl);
        } else {
            throw new IOException("Failed to download: " + responseCode);
        }
    }
}

