        HttpURLConnection con = (HttpURLConnection) new URL(reqURL).openConnection();
        con.setRequestMethod("GET");
        con.setRequestProperty("User-Agent", getUserAgent());
        try (InputStream in = con.getInputStream();
             OutputStream out = Files.newOutputStream(Paths.get(fileLocation))) {
            byte[] buffer = new byte[4096];
            int bytesRead;
            while ((bytesRead = in.read(buffer)) != -1) {
                out.write(buffer, 0, bytesRead);
            }
        }
    }

    public static String post(String reqURL, String postContent) throws IOException {
        HttpURLConnection con = (HttpURLConnection) new URL(reqURL).openConnection();
        con.setRequestMethod("POST");
        con.setRequestProperty("User-Agent", getUserAgent());
        con.setRequestProperty("Content-Type", "text/plain; utf-8");
        con.setDoOutput(true);
        try (OutputStream os = con.getOutputStream()) {
            byte[] input = postContent.getBytes("utf-8");
            os.write(input, 0, input.length);
        }
        return connectionToString(con);
    }

    public static String postForm(String reqURL, Map<String, String> fields) throws IOException {
        HttpURLConnection con = (HttpURLConnection) new URL(reqURL).openConnection();
        con.setRequestMethod("POST");
        con.setRequestProperty("User-Agent", getUserAgent());
        con.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
        con.setDoOutput(true);
        StringBuilder postData = new StringBuilder();
        for (Map.Entry<String, String> param : fields.entrySet()) {
            if (postData.length() != 0) postData.append('&');
            postData.append(URLEncoder.encode(param.getKey(), "UTF-8"));
            postData.append('=');
            postData.append(URLEncoder.encode(param.getValue(), "UTF-8"));
        }
        try (OutputStream os = con.getOutputStream()) {
            byte[] input = postData.toString().getBytes("utf-8");
            os.write(input, 0, input.length);
        }
        return connectionToString(con);
    }

    public static Stream<String> getLineStream(String reqURL) throws IOException {
        HttpURLConnection con = (HttpURLConnection) new URL(reqURL).openConnection();
        con.setRequestMethod("GET");
        con.setRequestProperty("User-Agent", getUserAgent());
        return new BufferedReader(new InputStreamReader(con.getInputStream())).lines();
    }

    public static String getUserAgent() {
        return "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.3";
    }

    private static String connectionToString(HttpURLConnection con) throws IOException {
        try (BufferedReader br = new BufferedReader(new InputStreamReader(con.getInputStream()))) {
            return br.lines().collect(Collectors.joining("\n"));
        }
    }
