         .option(ChannelOption.SO_BACKLOG, nettySoBacklog)
         .option(ChannelOption.SO_REUSEADDR, nettySoReuseaddr)
         .childOption(ChannelOption.TCP_NODELAY, nettyTcpNodelay)
         .childOption(ChannelOption.SO_KEEPALIVE, nettySoKeepalive)
         .childHandler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) throws Exception {
                 ch.pipeline().addLast(new NettyMQTTHandler(processor, props, sslCtxCreator));
             }
         });

        // Bind and start to accept incoming connections.
        ChannelFuture f = b.bind(props.getServerPort()).syncUninterruptibly();
        System.out.println("NettyAcceptor started on port " + props.getServerPort());
    }

    public void initializePlainTCPTransport(NettyMQTTHandler handler, IConfig props) {
        m_bossGroup = new NioEventLoopGroup();
        m_workerGroup = new NioEventLoopGroup();

        ServerBootstrap b = new ServerBootstrap();
        b.group(m_bossGroup, m_workerGroup)
         .channel(channelClass)
         .option(ChannelOption.SO_BACKLOG, nettySoBacklog)
         .option(ChannelOption.SO_REUSEADDR, nettySoReuseaddr)
         .childOption(ChannelOption.TCP_NODELAY, nettyTcpNodelay)
         .childOption(ChannelOption.SO_KEEPALIVE, nettySoKeepalive)
         .childHandler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) throws Exception {
                 ch.pipeline().addLast(handler);
             }
         });

        // Bind and start to accept incoming connections.
        ChannelFuture f = b.bind(props.getServerPort()).syncUninterruptibly();
        System.out.println("NettyAcceptor started on port " + props.getServerPort());
    }

    public void close() {
        if (m_workerGroup != null) {
            m_workerGroup.shutdownGracefully();
        }
        if (m_bossGroup != null) {
            m_bossGroup.shutdownGracefully();
        }
        System.out.println("NettyAcceptor shut down gracefully.");
    }
