        exitval = -1;
        error = null;

        try {
            process = new ProcessBuilder().command(command).redirectErrorStream(true).start();
            stdout = new BufferedReader(new InputStreamReader(process.getInputStream()));
        } catch (Exception e) {
            error = e.getCause().getMessage();
            return;
        }

        if (waitForExit) {
            waitForExit();
        }
    }

    public void waitForExit() {
        while (!checkForExit()) {
            if (stdoutAvailable()) {
                Log.d("AFWALL", "ShellCommand waitForExit [" + tag
                        + "] discarding read: " + readStdout());
            } else {
                try {
                    Thread.sleep(100);
                } catch (Exception e) {
                    Log.d("AFWall", "waitForExit", e);
                }
            }
        }
    }

    public void finish() {
        try {
            if (stdout != null) {
                stdout.close();
            }
        } catch (Exception e) {
            Log.e("AFWall", "Exception finishing [" + tag + "]", e);
        }

        if(process !=null) {
            process.destroy();
        }
        process = null;
    }

    public boolean checkForExit() {
        try {
            if(process != null) {
                exitval = process.exitValue();
            } else {
                finish();
            }
        } catch (IllegalThreadStateException e) {
            return false;
        }

        finish();
        return true;
    }

    public boolean stdoutAvailable() {
        try {
            return stdout.ready();
        } catch (java.io.IOException e) {
            Log.e("AFWall", "stdoutAvailable error", e);
            return false;
        }
    }

    public String readStdoutBlocking() {
        String line;
        if (stdout == null) {
            return null;
        }
        try {
            line = stdout.readLine();
        } catch (Exception e) {
            Log.e("AFWall", "readStdoutBlocking error", e);
            return null;
        }
        if (line == null) {
            return null;
        } else {
            return line + "\n";
        }
