        writeLine("Content-Type: application/octet-stream; name=\"" + file.getName() + "\"");
        writeLine("Content-Transfer-Encoding: base64");
        writeLine("Content-Disposition: attachment; filename=\"" + file.getName() + "\"");
        writeLine("");
        FileInputStream fis = new FileInputStream(file.getPath());
        byte[] buffer = new byte[4096];
        int bytesRead;
        while ((bytesRead = fis.read(buffer)) != -1) {
            out64.write(buffer, 0, bytesRead);
        }
        fis.close();
        out64.flush();
        writeLine("--" + boundary + "--");
    }

    public void closeConnection() throws IOException {
        if (connectedToMailServer) {
            writeLine(".");
            readWriteLine("QUIT");
            in.close();
            out.close();
            out64.close();
            socket.close();
            connectedToMailServer = false;
        }
    }

    private void readWriteLine(String command) throws IOException {
        writeLine(command);
        String response = in.readLine();
        if (!response.startsWith("2")) {
            throw new IOException("SMTP Error: " + response);
        }
    }

    private void writeLine(String line) throws IOException {
        out.write((line + "\r\n").getBytes());
        out.flush();
    }

    private String[] splitRecipientString(String recipientString, List<String> list) {
        return recipientString.split(",");
    }
