        socket = new Socket();
        tryAllAddresses(socket, hostname, port);
        connected = true;
        is = socket.getInputStream();
        os = socket.getOutputStream();
    }

    private void tryAllAddresses(Socket sock, String host, int port) throws IOException {
        InetAddress[] addresses = InetAddress.getAllByName(host);
        for (InetAddress addr : addresses) {
            try {
                sock.connect(new InetSocketAddress(addr, port));
                return;
            } catch (SocketTimeoutException ignored) {
            }
        }
        throw new SocketTimeoutException("Could not connect; socket timed out");
    }

    public void close() {
        connected = false;
        if (socket != null) {
            try {
                socket.close();
                socket = null;
            } catch (IOException e) {
                System.err.println("Error closing socket: " + e.getMessage());
