        if(msg.getDest() == null)
            flags += (byte)2;
        out.writeShort(Version.version);
        out.writeByte(flags);
        msg.writeTo(out);
    }

    protected static final Message readMessage(DataInput in) throws Exception {
        short ver = in.readShort();
        byte flags = in.readByte();
        Message msg = new BytesMessage();
