        if (instance == null) {
            instance = new Statsd();
        }
        return instance;
    }

    private void backgroundThread() {
        while (true) {
            try {
                StringBuilder batch = new StringBuilder();
                qManager.drainTo(batch, 100); // Batch up to 100 metrics
                if (batch.length() > 0) {
                    sendMetrics(batch.toString());
                }
            } catch (Exception ex) {
                // Handle exception
            }
        }
    }

    private void sendMetrics(String metrics) throws IOException {
        byte[] data = metrics.getBytes(CHARSET);
        DatagramPacket packet = new DatagramPacket(data, data.length, InetAddress.getByName("localhost"), 8125);
        try (DatagramSocket socket = new DatagramSocket()) {
            socket.send(packet);
        } catch (UnknownHostException e) {
            // Handle unknown host
        }
    }

    public void sendMetric(String metric) {
        qManager.offer(metric);
    }

    public void shutdown() {
        e.shutdown();
    }
