    private final int mPosition;
    private final HttpProxyCacheServer mCacheServer;
    private boolean mIsCanceled = false;
    private boolean mIsExecuted = false;
    private static final List<String> blackList = Collections.synchronizedList(new ArrayList<>());

    public PreloadTask(String rawUrl, int position, HttpProxyCacheServer cacheServer) {
        this.mRawUrl = rawUrl;
        this.mPosition = position;
        this.mCacheServer = cacheServer;
    }

    @Override
    public void run() {
        if (!mIsCanceled) {
            start();
        }
    }

    private void start() {
        mIsExecuted = true;
        try {
            String proxiedUrl = mCacheServer.getProxyUrl(mRawUrl);
            URL url = new URL(proxiedUrl);
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            connection.setConnectTimeout(5000); // 5 seconds timeout
            connection.connect();
            if (connection.getResponseCode() == HttpURLConnection.HTTP_OK) {
                // Preload content logic here
            } else {
                blackList.add(mRawUrl);
            }
        } catch (IOException e) {
            blackList.add(mRawUrl);
        }
    }

    public void executeOn(ExecutorService executorService) {
        executorService.submit(this);
    }

    public void cancel() {
        mIsCanceled = true;
    }
}
