    public HttpRequestManager(boolean proxyOn, String proxyHost, long proxyPort) {
        mProxyOn = proxyOn;
        mProxyHost = proxyHost;
        mProxyPort = proxyPort;
    }

    public void requestSubscribeServerConfigs(String urlStr, @NonNull Callback callback) {
        URL url;
        try {
            url = new URL(urlStr);
        } catch (MalformedURLException e) {
            e.printStackTrace();
            callback.onFailed();
            return;
        }
        HttpURLConnection connection = null;
        try {
            if (mProxyOn) {
                InetSocketAddress proxyAddress = new InetSocketAddress(mProxyHost, (int) mProxyPort);
                Proxy proxy = new Proxy(Proxy.Type.SOCKS, proxyAddress);
                connection = (HttpURLConnection) url.openConnection(proxy);
            } else {
                connection = (HttpURLConnection) url.openConnection();
            }
            connection.setReadTimeout(10000);
            connection.setConnectTimeout(10000);
            connection.setRequestMethod("GET");
            connection.setDoInput(true);
            connection.connect();
            int responseCode = connection.getResponseCode();
            if (responseCode != HttpsURLConnection.HTTP_OK) {
                callback.onFailed();
                return;
            }
            try (InputStream stream = connection.getInputStream()) {
                if (stream != null) {
                    @Nullable String response = DecodeUtils.decodeBase64(readStringFromStream(stream));
                    if (TextUtils.isEmpty(response)) {
                        callback.onFailed();
                    } else {
                        callback.onSuccess(response);
                    }
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
            callback.onFailed();
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private String readStringFromStream(InputStream inputStream) {
        try (Reader reader = new InputStreamReader(inputStream, StandardCharsets.UTF_8)) {
            char[] buf = new char[4096];
            StringBuilder sb = new StringBuilder();
            int readSize;
            while ((readSize = reader.read(buf)) != -1) {
                sb.append(buf, 0, readSize);
            }
            return sb.toString();
        } catch (IOException e) {
            e.printStackTrace();
        }
        return null;
    }

    public interface Callback {
        void onSuccess(String response);
        void onFailed();
    }
}

