public class FetchUrlMimeType {
    private DownloadManager.Request mRequest;
    private DownloadManager mDownloadManager;
    private String mUri;
    private String mCookies;
    private String mUserAgent;

    public enum Result {
        FAILURE_ENQUEUE,
        FAILURE_LOCATION,
        SUCCESS
    }

    public FetchUrlMimeType(DownloadManager downloadManager, DownloadManager.Request request, String uri, String cookies, String userAgent) {
        this.mDownloadManager = downloadManager;
        this.mRequest = request;
        this.mUri = uri;
        this.mCookies = cookies;
        this.mUserAgent = userAgent;
    }

    public Single<Result> create() {
        return Single.create(emitter -> {
            try {
                URL url = new URL(mUri);
                HttpURLConnection connection = (HttpURLConnection) url.openConnection();
                connection.setRequestMethod("GET");
                if (mCookies != null) {
                    connection.setRequestProperty("Cookie", mCookies);
                }
                if (mUserAgent != null) {
                    connection.setRequestProperty("User-Agent", mUserAgent);
                }
                connection.connect();

                String mimeType = connection.getContentType();
                String contentDisposition = connection.getHeaderField("Content-Disposition");

                String fileName = getFileNameFromHeader(contentDisposition);
                if (fileName == null) {
                    fileName = MimeTypeMap.getFileExtensionFromUrl(mUri);
                }

                mRequest.setMimeType(mimeType);
                mRequest.setTitle(fileName);
                mRequest.setDescription("Downloading " + fileName);
                mRequest.setNotificationVisibility(DownloadManager.Request.VISIBILITY_VISIBLE_NOTIFY_COMPLETED);
                mRequest.setDestinationInExternalPublicDir(Environment.DIRECTORY_DOWNLOADS, fileName);

                long downloadId = mDownloadManager.enqueue(mRequest);
                emitter.onSuccess(Result.SUCCESS);
            } catch (IOException e) {
                emitter.onError(e);
            } catch (Exception e) {
                emitter.onError(e);
            }
        });
    }

    private String getFileNameFromHeader(String header) {
        if (header == null) {
            return null;
        }
        String[] parts = header.split(";");
        for (String part : parts) {
            if (part.trim().startsWith("filename")) {
                return part.substring(part.indexOf('=') + 1).trim().replace("\"", "");
            }
        }
        return null;
    }
}
